@import url('https://fonts.googleapis.com/css?family=Poppins:400,500,600,700&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Poppins', sans-serif;
    letter-spacing: 0.5px;
}

body {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    background: #f6f6f6;
}

.box {
    /*margin-left: -17px;*/
    display: none;
    position: relative;
    width: 600px;
    border-bottom: 20px solid #03a9f2;
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
}

.box h2,
.box h4
{
    color: #fff;
    font-weight: normal;
    background: #03a8f4;
    padding: 10px 20px;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
}


.box ul {
    position: relative;
    background: #fff;
}

.box ul:hover li {
    opacity: 0.2;
}

.box ul li {
    list-style-type: none;
    padding: 10px;
    width: 100%;
    background: #fff;
    box-shadow: 0 5px 25px rgba(0,0,0,0.1);
    transition: all 0.1s;
}

.box ul li:hover {
    transform: scale(1.02);
    z-index: 100;
    /*background: #25bcff;*/
    /*color: white;*/
    opacity: 1;
}

.box ul li span {
    width: 20px;
    height: 20px;
    text-align: center;
    line-height: 20px;
    background: #25bcff;
    color: #fff;
    display: inline-block;
    border-radius: 50%;
    margin-right: 10px;
    font-size: 12px;
    font-weiht: 600;
    transform: translateY(-2px);
}

.box ul li:hover span {
    background-color: #25bcff;
    /*background: #fff;*/
    color: white;
}

@media screen and (max-width: 674px) {
    .box {
        width: 500px;
    }
    .box h2 {
        font-size: 1.2em;
    }
}

@media screen and (max-width: 560px) {
    .box {
        width: 400px;
    }
    .box h2 {
        font-size: 1.1em;
    }
    .box li {
        font-size: 1em;
    }
}

@media screen and (max-width: 420px) {
    .box {
        width: 350px;
    }
}

@media screen and (max-width: 370px) {
    .box {
        width: 300px;
    }
}
